.navigation {
	&__checbox {
		display: none;
	}

	&__button {
		background-color: white;
		height: 4rem;
		width: 4rem;
		position: fixed;
		top: 6rem;
		right: 6rem;
		border-radius: 50%;
		z-index: 2000;
		box-shadow: 0 1rem 3rem rgba(0, 0, 0, .1);
		text-align: center;
		cursor: pointer;
	}

	&__background {
		height: 3rem;
		width: 3rem;
		border-radius: 50%;
		position: fixed;
		top: 6.5rem;
		right: 6.5rem;
		background-image: radial-gradient($light-blue, $dark-blue);
		z-index: 1000;
		transition: transform .8s cubic-bezier(0.85, 0, 0.07, 1);

		///transform: scale(50);
	}

	&__nav {
		height: 100vh;
		width: 100%;
		position: fixed;
		top: 0;
		right: 0;
		z-index: 1500;

		opacity: 0;
		width: 0;
		transition: all .8s cubic-bezier(0.68, -0.55, 0.265, 1.55);
	}

	&__list {
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		list-style: none;
		text-align: center;
		width: 100%;
	}

	&__item {
		margin: 1rem;
	}

	&__link {
		&:link,
		&:visited {
			display: inline-block;
			font-size: 2.2rem;
			font-weight: 300;
			padding: .5rem 2rem;
			color: white;
			text-decoration: none;
			text-transform: uppercase;
			background-image: linear-gradient(120deg, transparent 0%, transparent 50%, white 50%);
			background-size: 230%;
			transition: all .4s;

			span {
				margin-right: 1rem;
				display: inline-block;
			}
		}

		&:hover,
		&:active {
			background-position: 100%;
			color: $medium-blue;
			transform: translateX(1rem);
		}
	}

	&__checbox:checked ~ &__background {
		transform: scale(80);
	}

	&__checbox:checked ~ &__nav {
		opacity: 1;
		width: 100%;
	}

	/////

	&__icon {
		position: relative;
		margin-top: 2rem;

		&,
		&::before,
		&::after {
			width: 2rem;
			height: 2px;
			background-color: $dark-blue;
			display: inline-block;
		}

		&::before,
		&::after {
			content: "";
			position: absolute;
			left: 0;
			transition: all .7s;
		}

		&::before {
			top: -0.5rem;
		}
		&::after {
			top: .5rem;
		}
	}

	&__button:hover &__icon::before {
		top: -1rem;
	}

	&__button:hover &__icon::after {
		top: 1rem;
	}

	&__checbox:checked + &__button &__icon {
		background-color: transparent;
	}

	&__checbox:checked + &__button &__icon::before {
		top: 0;
		transform: rotate(45deg);
	}

	&__checbox:checked + &__button &__icon::after {
		top: 0;
		transform: rotate(-45deg);
	}
}

