@keyframes moveinleft {
	0% {
		opacity: 0;
		transform: translatex(-100px);
	}
	80% {
		transform: translatex(10px);
	}
	100% {
		opacity: 1;
		transform: translate(0);
	}
}

@keyframes moveinright {
	0% {
		opacity: 0;
		transform: translatex(100px);
	}
	80% {
		transform: translatex(-10px);
	}
	100% {
		opacity: 1;
		transform: translate(0);
	}
}

@keyframes moveinbottom {
	0% {
		opacity: 0;
		transform: translatey(30px);
	}
	100% {
		opacity: 1;
		transform: translate(0);
	}
}

