		opacity: 0;
		transform: translate(-50%, -50%) scale(.5);
		transition: all .4s .2s;
	}

	&__left {
		width: 33.333333%;
		display: table-cell;
	}

	&__right {
		width: 66.666667%;
		display: table-cell;
		vertical-align: middle;
		padding: 3rem 5rem;
	}

	&__img {
		display: block;
		width: 100%;
	}

	&__text {
		font-size: 1rem;

		-moz-column-count: 2;
		-moz-column-gap: 2rem;
		-moz-column-rule: 1px solid wheat;

		column-count: 2;
		column-gap: 2rem;
		column-rule: 1px solid wheat;

		-moz-hyphens: auto;
		-ms-hyphens: auto;
		-webkit-hyphens: auto;
		hyphens: auto;
	}

	&:target &__content {
		opacity: 1;
		transform: translate(-50%, -50%) scale(1);
	}

	&:target {
		opacity: 1;
		visibility: visible;
	}

	&__close {
		&:link,
		&:visited {
			color: $dark-blue;
			position: absolute;
			top: 2rem;
			right: 2.5rem;
			font-size: 2rem;
			text-decoration: none;
			display: inline-block;
			transition: all .3s;
			line-height: 1;
		}

		&:hover {
			color: red;
		}
	}
}

